package com.core;

public class SelectiveProg3 {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		int a=1000;
		int b=600;
		int c=50;
		
		if((a>b)&&(a>c))
		{
			System.out.println("A is Big");
		}
		else if((b>a)&&(b>c))
		{
			System.out.println("B is big");
		}
		else {
			System.out.println("C is big");
			
		}
			
			
	}

	

}
